import { Component, Input } from '@angular/core';
import datajson from '../../assets/tables.json';

@Component({
  selector: 'app-explore',
  templateUrl: './explore.component.html',
  styleUrls: ['./explore.component.css']
})
export class ExploreComponent {
  @Input() data : any;
  active!:number;
  lastIndexPosition: number = 0;

  ngOnInit():void{
    this.data = datajson;
    this.lastIndexPosition+=1;
  }
  onNext() {
    if (this.active >= this.lastIndexPosition) { this.active = 0; }
    else { this.active += 1; }
  }
  onPrevious() {
    if (this.active == 0) { this.active = this.lastIndexPosition; }
    else { this.active -= 1; }
  }

}
